import React, {Component} from 'react';
import './App.css';
import Navigation from './Components/Navigation/Navigation';
import Logo from './Components/Logo/Logo';
import Rank from './Components/Rank/Rank';
import FaceRecognition from './Components/FaceRecognition/FaceRecognition';
import ImageLinkForm from './Components/ImageLinkForm/ImageLinkForm';
import Particles from 'react-particles-js';
import clarifai from 'clarifai';
import SignIn from './Components/SignIn/SignIn';
import Register from './Components/Register/Register';


const app = new clarifai.App({
  apiKey: 'e7160608536542de948643311433b259'
});
const particleparams = {
                particles: {
                  number: {
                    value: 200,
                    density: {
                      enable: true,
                      value_area: 1100
                    }
                  }
              }}

class App extends Component {
  constructor(){
    super();
    this.state = {
      input: '',
      imageUrl: '',
      box: {},
      route: 'signin',
      isSignedIn: false
    }
  }


  onRouteChange = (droute) => {
    this.setState({route: droute});
    this.state.route==='home' ? this.setState({isSignedIn:true})
    :  this.setState({isSignedIn:false})
  }

  calculateFaceLocation = (data) => {
    const face = data.outputs[0].data.regions[0].region_info.bounding_box;
    const image = document.getElementById('inputimage');
    const width = Number(image.width);
    const height = Number(image.height);
    return {
      leftCol: face.left_col*width,
      topRow: face.top_row*height,
      rightCol: width-face.right_col*width,
      bottomRow: height-face.bottom_row*height
    }

  }

  displayBox = (box) =>{
    this.setState({box: box});
    console.log(this.state.box)
  }

  onSubmit =()=>{
    this.setState({imageUrl: this.state.input});
    app.models.predict(clarifai.FACE_DETECT_MODEL, 
      this.state.input).then((response) => this.displayBox(this.calculateFaceLocation(response)))
      .catch(err => console.log(err));

  }

  onInputChange = (event) => {
    this.setState({
      input: event.target.value,
    })

  }

  render(){

    return (

    <div className="App ">
      <Particles className='particles'  
                params={particleparams} />
      <Navigation onRouteChange={this.onRouteChange} isSignedIn={this.state.isSignedIn}/>
      { this.state.route==='signin'
      ? <SignIn onRouteChange= {this.onRouteChange}/> 
      : this.state.route==='register' ? <Register onRouteChange={this.state.onRouteChange}/>
      : <div> 
      <Logo />
      <Rank />
      <ImageLinkForm 
      onInputChange={this.onInputChange} 
      onSubmit={this.onSubmit}/>
      <FaceRecognition box={this.state.box} 
      imageUrl={this.state.imageUrl}/>
      </div>
      }
    </div>
  );
  }

}

export default App;
